mapper.update:
update
t_article set click_number = click_number + 1 where status = 1 and id
= #{id}

update t_article
set version = version + 1,update_at = now(),star = #{star} where id =
#{id} and version = #{version}

mapper.select:
select a.id,a.title,a.cover,a.`level`,a.click_number as
clickNumber,a.tag,a.create_at as createAt
from t_article a left join s_dict_item i on a.type = i.id
where a.status = 1 and a.show_type = 0

	and i.id = #{type}

limit #{page},#{pageCount}

select t.id,t.title,t.content,t.url,i.`value` as
type,t.star,t.create_at as createAt,t.tag from t_article t left join
s_dict_item i on i.id = t.type where t.status = 1 and t.id = #{id}

mapper.update:
update
t_article_comment_favour set status = #{status},version = version +
1,update_at = now() where id = #{id} and version = #{version}
